import { useState} from 'react'
import Header from './components/Header'
import Tasks from './components/Tasks'
const App = () => {
  const [tasks, setTasks] = useState([
      {
          id: 1,
          text: "eat snacks",
          day: "Feb 5, at 2:30pm",
          reminder: false,
      },
      {
          id: 2,
          text: "exercise",
          day: "Feb 7, at 2:30pm",
          reminder: false,
      },
      {
          id: 3,
          text: "call mom",
          day: "Feb 20, at 4:30pm",
          reminder: false,
      },
    ]
  )

  // Delete Tasks

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id))
    console.log('delete', id)
  }

  // Toggle Reminder for incomplete tasks
  return (
    // // only one single parent element can be reutnr in the div
    // we can pass anything we want in to retrieve a prop
      // js can be written right in here
    <div className = "container" >

      <Header / >
      {tasks.length > 0 ? <Tasks  tasks={tasks} onDelete={deleteTask}/ >: 'All done! No tasks to show here :)'}
    </div>
  )
}
